declare type ScrollTarget = Document | HTMLElement;
interface IScrollPosition {
    x: number;
    y: number;
}
export interface IScrollListener {
    dependencies: ScrollTarget[];
    handler: (delta: IScrollPosition) => void;
}
declare const _default: {
    add(listener: IScrollListener): void;
    remove(listener: IScrollListener): void;
};
export default _default;
